import  {useQuery, gql} from '@apollo/client'
import { useEffect } from 'react'
import { DescriptionContainer, ImageContainer, SingleBox, TittleContainer } from './styled'

const GET_ITEMS = gql`
    query{
        getItems{
            data{
                description
                image
                title
                id
            }
        }
    }
`

const getSingleDivArray = ()=>{

}


const DisplayTable = ()=>{
    const  {loading,error,data} = useQuery(GET_ITEMS)   
    if (error) return `Error : ${error}`
    let divArray;
    if (!loading){
        divArray = data.getItems.data.map((element)=>{
            <SingleBox>
                <ImageContainer imgURL={element.image}></ImageContainer>
                <TittleContainer> {element.tittle} </TittleContainer>
                <DescriptionContainer> {element.description} </DescriptionContainer>
            </SingleBox>
        })
    }


    return(
        <div style={{display : "flex", flexWrap:"wrap"}}>
            {divArray}
        </div>
    )
}

export default DisplayTable;
